import React, { Component } from "react";
import { Header, Message, Table } from "semantic-ui-react";

import { API } from "../config";

import "../css/Jornadas.css";

class Jornadas extends Component {
  constructor(props) {
    super(props);
    this.state = {
      jornadas: null,
      isLoading: null,
    };
  }

  componentDidMount() {
    this.getJornadas();
  }

  async getJornadas() {
    if (!this.state.jornadas) {
      try {
        this.setState({ isLoading: true });
        const response = await fetch(API + "/jornadas", {
          headers: {},
        });
        const ListJornadas = await response.json();
        this.setState({ jornadas: ListJornadas.data, isLoading: false });
      } catch (err) {
        this.setState({ isLoading: false });
        console.error(err);
      }
    }
  }

  state = {};
  render() {
    return (
      <div>
        <Header as="h1">Jornadas</Header>
        {this.state.isLoading && <Message info header="Cargando Jornadas..." />}
        {this.state.players && (
          <Table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Fecha</th>
                <th>Localidad</th>
                <th>Municipio</th>
                <th>idEstado</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {this.state.jornadas.map((player) => (
                <tr id={player.id} key={player.id}>
                  <td>{player.id}</td>
                  <td>{player.name}</td>
                  <td>{player.answers}</td>
                  <td>{player.points}</td>
                  <td>Action buttons placeholder</td>
                </tr>
              ))}
            </tbody>
            <tfoot>
              <tr>
                <th>Nombre</th>
                <th>Fecha</th>
                <th>Localidad</th>
                <th>Municipio</th>
                <th>idEstado</th>
                <th>Acciones</th>
              </tr>
            </tfoot>
          </Table>
        )}
      </div>
    );
  }
}

export default Jornadas;
