import React, { Component } from "react";
import { Header, Message, Table, Button } from "semantic-ui-react";

import { API } from "../config";
import AddJornada from "./AddJornada";

import "../css/Jornadas.css";

class Jornadas extends Component {
  constructor(props) {
    super(props);
    this.state = {
      jornadas: null,
      isLoading: null,
    };
    this.onAddition = this.onAddition.bind(this);
  }

  onAddition(jornada) {
    this.setState({
      jornadas: [...this.state.jornadas, jornada],
    });
  }

  componentDidMount() {
    this.getJornadas();
  }

  async getJornadas() {
    if (!this.state.jornadas) {
      try {
        this.setState({ isLoading: true });
        const response = await fetch(API + "/jornadas", {
          headers: {},
        });
        const ListJornadas = await response.json();
        this.setState({ jornadas: ListJornadas.data, isLoading: false });
        console.log(this.state.jornadas);
      } catch (err) {
        this.setState({ isLoading: false });
        console.error(err);
      }
    }
  }

  state = {};
  render() {
    return (
      <div>
        <Header as="h1">Jornadas</Header>
        <Button>Agregar jornada</Button>
        {this.state.isLoading && <Message info header="Cargando Jornadas..." />}
        {this.state.jornadas && (
          <div>
            <Table>
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Fecha</th>
                  <th>Localidad</th>
                  <th>Municipio</th>
                  <th>idEstado</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {this.state.jornadas.map((jornada) => (
                  <tr id={jornada.id} key={jornada.id}>
                    <td>{jornada.nombre}</td>
                    <td>{jornada.fecha}</td>
                    <td>{jornada.localidad}</td>
                    <td>{jornada.municipio}</td>
                    <td>{jornada.idEstado}</td>
                    <td>
                      <Button>Eliminar Jornada</Button>
                    </td>
                  </tr>
                ))}
              </tbody>
              <tfoot>
                <tr>
                  <th>Nombre</th>
                  <th>Fecha</th>
                  <th>Localidad</th>
                  <th>Municipio</th>
                  <th>idEstado</th>
                  <th>Acciones</th>
                </tr>
              </tfoot>
            </Table>
            <AddJornada />
          </div>
        )}
      </div>
    );
  }
}

export default Jornadas;
